import React , {useEffect , useState} from 'react';
import "./PersonalDetails.css";
import { Formik } from 'formik';

function PersonalDetails() {
  const[countryList , setCountryList] = useState([]);
  const [selectedCountry , setSelectedCountry] = useState("");
  const [selectedCountryCode , setSelectedCountryCode] = useState("");
  useEffect(() =>{
    const fetchData = async() => {
      const data = await fetch("https://restcountries.eu/rest/v2/all");
      const items = await data.json();
      console.log(items)
      setCountryList(items)
    }
    fetchData()
  } , [])

  useEffect(() =>{
  let filtered =   countryList.filter(country =>{ return (country == selectedCountry)})
  console.log(filtered)
  } , [selectedCountry])
 
    return (
        <div>
           i am personal 

     <Formik
       initialValues={{ email: '', password: '' , country: '',}}
       validate={values => {
         const errors = {};
         if (!values.email) {
           errors.email = 'Required';
         } else if (
           !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
         ) {
           errors.email = 'Invalid email address';
         }
         return errors;
       }}
       onSubmit={(values, { setSubmitting }) => {
         setTimeout(() => {
           alert(JSON.stringify(values, null, 2));
           setSubmitting(false);
         }, 400);
         setSelectedCountry(values.country)
         
       }}
     >
       {({
         values,
         errors,
         touched,
         handleChange,
         handleBlur,
         handleSubmit,
         isSubmitting,
         /* and other goodies */
         
       }) => (
         <form onSubmit={handleSubmit}>
           <input
             type="email"
             name="email"
             onChange={handleChange}
             onBlur={handleBlur}
             value={values.email}
           />
           {errors.email && touched.email && errors.email}
           <input
             type="password"
             name="password"
             onChange={handleChange}
             onBlur={handleBlur}
             value={values.password}
           />
           {errors.password && touched.password && errors.password}
          
           {/* my own form */}
  

   
    
     <select name="country" class="form-control" id="country" name="country" value={values.country}  onChange={handleChange} >
        <option value="0" label="Select a country ... " selected="selected">Select a country ... </option>
      
           
            
          
     {countryList.map(single => (
        
      <option  value={single.name} label={single.name}>{single.name}</option>
     ))}
       </select>



       <button type="submit" disabled={isSubmitting}>
             Submit
           </button>
         </form>
       )}
       </Formik>
       <h1>{selectedCountry}</h1>
       <h1>{selectedCountryCode}</h1>
   </div>
 );
 
  
    
       }

export default PersonalDetails
