 function EmailVerification(){
        //  const[enteredOTP , setEnteredOTP] = useState('')
         const [generatedOTP , setGeneratedOTP] = useState("")
          const [ errs , setErrs] = useState("")
          const[OTP , setOTP] = useState()
          let history = useHistory()
         useEffect(() =>{
          var password = generator.generate({
            length: 5,
            numbers: true
          });
          
          // 'uEyMTw32v9'
          setGeneratedOTP(password)
          console.log(password);
         }, [])
    //    const  handleChange = ( e) => {
         
    //     setOtp(e.target.value)
    // };
    const verifyHandler = (e) => {
      e.preventDefault();

      if(generatedOTP == OTP){
        // window.location = "/success"
        history.push("/success")
      }
      else{
        setErrs("Check Entered otp again")
        setOTP("")
      }
    }
       return(
         <div>
            <NavThree />
        
           <div className="emailVerification">
            
                  <center>
        <Typography variant="h5">Enter the OTP</Typography>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Est, illum?
        </p>
      </center>
                <p>Enter your code : <span style={{fontSize:"25px"}}>{generatedOTP}</span>  </p>
         
           {/* <div>
              <OtpInput
                style={{ width: "3em", height: "3em" }}
                onChange={(otp) => console.log(otp)}
                numInputs={5}
                separator={<span>&nbsp;&nbsp;</span>}
              />{" "}
            </div> */}
            {/* <div className="single-input">
              <label htmlFor="otp">Enter OTP</label>
            <input type="text"  className="otp-input" name="otp" value={enteredOTP} onChange={(e) => setEnteredOTP(e.target.value)}/>
            </div> */}
            <div className="single-input">
            <OtpInput            
            inputStyle={{  
                width: '2.5rem',  
                height: '3rem',  
                margin: '10px 2rem',  
                fontSize: '1rem',  
                borderRadius: 4,  
                border: '2px solid rgba(0,0,0,0.3)',
                alignItems: 'center',                      
            }}  
          onChange={setOTP}
          numInputs={5}
          separator={<span> &nbsp; </span>}
          value={OTP}
        />    
            </div>
            {errs}
           <div className="buttons">
<Link to="/companydetails">
<button type="submit"  className="back-btn">
                        Back
                      </button>
</Link>
           
                      <button type="submit" className="next-btn" onClick={verifyHandler}>
                      Verify
                      </button>
           </div>
          
         
           </div>
           <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Quae, aut.  <span style={{ color: "orange" }}>
          name@doamin.com
        </span></p>
           </div>
       )
}

export default EmailVerification;
