import React from 'react';
import ImageUploading from 'react-images-uploading';
import "./ImageUpload.css"
 
 function ImageUpload() {
  const [images, setImages] = React.useState([]);
  const maxNumber =1;
 
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };
 
  return (
    <div className="imageUpload">
      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="data_url"
      >
        {({
          imageList,
          onImageUpload,
        //   onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps,
        }) => (
          // write your building UI
        //   <div className="upload__image-wrapper">
        <div>
        <div>
            <button
            //   style={isDragging ? { color: 'red' } : undefined}
              onClick={onImageUpload}
              {...dragProps}
              className="image-update-btn"
            >
              Upload your company logo
            </button>
            &nbsp;
            <img src="https://i.pinimg.com/736x/ec/14/7c/ec147c4c53abfe86df2bc7e70c0181ff.jpg" alt="" />
            </div>
           
            {/* <button onClick={onImageRemoveAll}>Remove all images</button> */}
            <div className="image-img">
                     {imageList.map((image, index) => (
                         <>
              <div key={index} className="image-item">
                <img src={image['data_url']} alt="" width="100" className="image-image"/>
                </div>
                
                
               
                <div className="image-item__btn-wrapper">
                  <button  className="image-btn" onClick={() => onImageUpdate(index)}>Update</button>
                  <button  className="image-btn" onClick={() => onImageRemove(index)}>Remove</button>
                </div>
                </>
             ))}
              </div>
            
          </div>
        )}
      </ImageUploading>
    </div>
  );
}

export default ImageUpload;
